void config()
{
    for(int16_t i = 0; i < IN_VAR_DEPTH; i++) {
        cgra_input[0][i] = In_Img[i*3];
        cgra_input[1][i] = In_Img[i*3 + 1];
        cgra_input[2][i] = In_Img[i*3 + 2];
    }

}

void software(void)
{
    conv2D( outSW );
}

uint32_t check(void)
{
    uint32_t errors = 0;

    for(int16_t i = 0; i < IN_VAR_DEPTH; i++) {
        outCGRA[4 + i*3] = cgra_output[3][i];
    }

    for( uint16_t i = 0; i < IMG_DIM; i++ ){
            PRINTF("[%d] %d != %d\n", i, outSW[i],outCGRA[i] );
        // if( outImg[i] != Gold_Out_Img[i] ){
        //     errors++;
        // }


    }

    o_ret_cgra = outCGRA[4];

    return errors;
}

