/*                            LOCAL FUNCTIONS                               */
/**                                                                        **/
/****************************************************************************/

void config()
{
	for(int i = 0; i < 40; i++ )
		x[i] = kcom_getRand() % (32767 - -32768 + 1) + -32768;
	cgra_input[1][0] = x;
	cgra_input[3][0] = 32767;
	cgra_input[3][1] = -32768;

}

void software(void) 
{
    ret_soft = gsm( x );
}

uint32_t check(void) 
{
    uint32_t errors = 0;
    
	ret_cgra = cgra_output[3][0];


    for( int i = 0; i < 1; i++ )
    {
        if (ret_cgra != ret_soft) {
            errors++;
        }
    }
    return errors;
}

